#pragma once#include <string>#include <vector>#include "Country.h"class Continent{public:Continent();Continent(std::string name, int ID, int bonus);~Continent();std::string get_continent_name(){ return continent_name; }void setContainedCountries(std::vector<Country*>& contained) {for (unsigned int i = 0; i < contained.size(); i++) {contained.at(i)->set_continent_name(get_continent_name());}containedCountries = contained; }std::vector<Country*> getContainedCountries() { return containedCountries; }void PrintContainedCountries();private:int ID;std::string continent_name;std::vector<Country*> containedCountries;};