#include "MapLoader.h"MapLoader::MapLoader(){}MapLoader::~MapLoader(){}void MapLoader::Save(MapTemplate mapTemplate, Constants::MapExtension mapExtension){if (mapExtension == Constants::MapExtension::TXT){        MapLoader tml;tml.Save(mapTemplate);}else if (mapExtension == Constants::MapExtension::CONQUEST){ConquestMapLoaderSaver cmls;cmls.Save(mapTemplate);}}MapTemplate MapLoader::Load(std::string mapName){MapTemplate mapTemplate;string dirname_txt = Constants::maps_directory + mapName + "\\countries.txt";string dirname_conquest = Constants::maps_directory + mapName + "\\" + mapName + ".map";std::fstream filestr;    filestr.open(dirname_txt);while (filestr.is_open()) {filestr.close();        MapLoader tml;mapTemplate = tml.Load(mapName);}return mapTemplate;}